import React, { Component, PropTypes } from 'react';
import MixinDecorator from '../../src/index';
import SomeMixin from '../mixins/some-mixin';

export const SomeDecorator = MixinDecorator(
  'SomeDecorator',
  SomeMixin,
  {
    contents: 'yo',
    controlledByMixin: SomeMixin.controlledByMixin
  },
  [
    'click'
  ]
);

@SomeDecorator
export default class Test extends Component {
  static propTypes = {
    contents: PropTypes.string.isRequired,
    controlledByMixin: PropTypes.func.isRequired
  };

  render() {
    const { contents, controlledByMixin } = this.props;

    return (
      <div className="test">
        <div>{contents}</div>
        <div>{controlledByMixin()}</div>
      </div>
    );
  }
}
